function updateModel(e,l,o,t){switch(l){case COLOR:e.updateMaterialColor(o,t);break;case TEXTURE:e.updateMaterialTexture(o,t)}}function addonClick(e,l){$(e).on("click",l)}function getTextureURL(e){return cors_anywhere+$(e).css("background-image").slice(4,-1).replace(/["']/g,"")}function handleCycleColor(){let e=document.querySelector("#cycle-viewer");addonClick("#red-color-btn",()=>{updateModel(e,COLOR,"red body","red")}),addonClick("#yellow-color-btn",()=>{updateModel(e,COLOR,"red body","yellow")}),addonClick("#green-color-btn",()=>{updateModel(e,COLOR,"red body","#00ff2f")}),addonClick("#black-handle-color-btn",()=>{updateModel(e,COLOR,"black metal","black")}),addonClick("#white-handle-color-btn",()=>{updateModel(e,COLOR,"black metal","white")}),addonClick("#grey-handle-color-btn",()=>{updateModel(e,COLOR,"black metal","grey")})}function handlePhoneCase(){let e=document.querySelector("#case-viewer");addonClick("#upload-texture-btn",()=>{$("#case-texture").click()}),$("#case-texture").on("change",l=>{let o=l.target.files[0];updateModel(e,TEXTURE,"Image MAT",URL.createObjectURL(o)),$("#upload-texture-btn").css("background-image",`url("${URL.createObjectURL(o)}")`)})}function handleCouch(){let e=document.querySelector("#couch-viewer");addonClick("#couch-metallic",()=>{updateModel(e,TEXTURE,"Leather",getTextureURL("#couch-metallic"))}),addonClick("#couch-tiger",()=>{updateModel(e,TEXTURE,"Leather",getTextureURL("#couch-tiger"))}),addonClick("#couch-wood",()=>{updateModel(e,TEXTURE,"Leather",getTextureURL("#couch-wood"))}),addonClick("#pillow-grey",()=>{updateModel(e,TEXTURE,"Pillow1",getTextureURL("#pillow-grey"))}),addonClick("#pillow-fun",()=>{updateModel(e,TEXTURE,"Pillow1",getTextureURL("#pillow-fun"))}),addonClick("#pillow-red",()=>{updateModel(e,TEXTURE,"Pillow1",getTextureURL("#pillow-red"))})}const cors_anywhere="https://cors-anywhere.herokuapp.com/",COLOR=0,TEXTURE=1;handleCycleColor(),handlePhoneCase(),handleCouch();